import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Calendar } from 'primereact/calendar';
import { Dropdown } from 'primereact/dropdown';
import { InputNumber } from 'primereact/inputnumber';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';
import { InputTextarea } from 'primereact/inputtextarea';

// Inline styles with hardcoded colors based on your brand colors
const styles = {
  container: {
    backgroundColor: '#FFFFFF', /* White background for the card */
    margin: '20px auto',
    width: '90%',
    maxWidth: '600px',
    padding: '2rem',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    fontFamily: "'Open Sans', sans-serif"
  },
  title: {
    color: '#DB0B00', /* Rosso Corsa for the title */
    fontFamily: "'Open Sans Bold', sans-serif"
  },
  label: {
    fontWeight: 'bold',
    marginBottom: '0.5rem',
    display: 'block',
    color: '#000000' /* Default black for label text */
  },
  button: {
    backgroundColor: '#DB0B00', /* Rosso Corsa for buttons */
    color: '#FFFFFF', /* White text for button */
    border: 'none',
    padding: '10px 20px',
    borderRadius: '4px',
    fontSize: '1rem',
    fontFamily: "'Open Sans Semibold', sans-serif",
    cursor: 'pointer'
  },
  buttonHover: {
    backgroundColor: '#FFB4E1', /* Light Hot Pink for button hover */
  },
  formField: {
    marginBottom: '1rem'
  }
};

const BookingForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    contactName: '',
    contactEmail: '',
    eventDate: null,
    eventTime: null,
    eventType: '',
    otherFoodTrucks: 'no',
    attendees: 0,
    additionalInfo: ''
  });

  const eventTypes = [
    { label: 'Corporate', value: 'corporate' },
    { label: 'Wedding', value: 'wedding' },
    { label: 'Birthday', value: 'birthday' },
    { label: 'Festival', value: 'festival' },
    { label: 'Other', value: 'other' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);  // Placeholder for now. This will send data to the backend.
    alert('Booking request submitted with placeholder data!');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Book Your Event</h2>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="companyName">Company Name</label>
        <InputText id="companyName" name="companyName" value={formData.companyName} onChange={handleChange} required />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="contactName">Contact Name</label>
        <InputText id="contactName" name="contactName" value={formData.contactName} onChange={handleChange} required />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="contactEmail">Contact Email</label>
        <InputText id="contactEmail" name="contactEmail" value={formData.contactEmail} onChange={handleChange} required />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventDate">Event Date</label>
        <Calendar id="eventDate" name="eventDate" value={formData.eventDate} onChange={(e) => setFormData({ ...formData, eventDate: e.value })} showIcon />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventTime">Event Time (Range)</label>
        <Calendar id="eventTime" name="eventTime" value={formData.eventTime} onChange={(e) => setFormData({ ...formData, eventTime: e.value })} timeOnly hourFormat="24" showTime />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventType">Event Type</label>
        <Dropdown id="eventType" name="eventType" value={formData.eventType} options={eventTypes} onChange={(e) => setFormData({ ...formData, eventType: e.value })} placeholder="Select Event Type" />
      </div>

      <div style={styles.formField}>
        <label style={styles.label}>Will there be other food trucks or food offered at the event?</label>
        <div className="p-formgroup-inline">
          <RadioButton inputId="otherFoodTrucksYes" name="otherFoodTrucks" value="yes" onChange={handleChange} checked={formData.otherFoodTrucks === 'yes'} />
          <label htmlFor="otherFoodTrucksYes">Yes</label>
          <RadioButton inputId="otherFoodTrucksNo" name="otherFoodTrucks" value="no" onChange={handleChange} checked={formData.otherFoodTrucks === 'no'} />
          <label htmlFor="otherFoodTrucksNo">No</label>
        </div>
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="attendees">Expected Number of Attendees</label>
        <InputNumber id="attendees" name="attendees" value={formData.attendees} onChange={(e) => setFormData({ ...formData, attendees: e.value })} required />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="additionalInfo">Additional Information</label>
        <InputTextarea id="additionalInfo" name="additionalInfo" value={formData.additionalInfo} onChange={handleChange} />
      </div>

      <Button label="Submit" icon="pi pi-check" onClick={handleSubmit} style={styles.button} />
    </div>
  );
};

export default BookingForm;
