import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Calendar } from 'primereact/calendar';
import { Dropdown } from 'primereact/dropdown';
import { InputNumber } from 'primereact/inputnumber';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';
import { InputTextarea } from 'primereact/inputtextarea';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    contactName: '',
    contactEmail: '',
    eventDates: null,
    eventTime: null,
    eventType: '',
    otherFoodTrucks: 'no',
    attendees: 0,
    additionalInfo: ''
  });

  // Define date range logic (Min/Max)
// Define date range logic (Min/Max)
const today = new Date();

// Set minDate to one week in the future
const minDate = new Date();
minDate.setDate(today.getDate() + 7);

// Set maxDate to two years in the future
const maxDate = new Date();
maxDate.setFullYear(today.getFullYear() + 2);
  
  const eventTypes = [
    { label: 'Corporate', value: 'corporate' },
    { label: 'Wedding', value: 'wedding' },
    { label: 'Birthday', value: 'birthday' },
    { label: 'Festival', value: 'festival' },
    { label: 'Other', value: 'other' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Booking request submitted!');
  };

  const customStyles = `
  .p-inputtext {
    color: #DB0B00 !important; /* Rosso Corsa */
    font-family: 'Open Sans', sans-serif !important;
    font-style: italic !important;
    font-weight: 700 !important;
    font-size: 16px !important;
  }
  .p-dropdown-label, .p-inputnumber-input {
    color: #DB0B00 !important; /* Rosso Corsa */
    font-family: 'Open Sans', sans-serif !important;
    font-style: italic !important;
    font-weight: 700 !important;
    font-size: 16px !important;
  }
  .p-calendar {
    width: 100%;
  }
  .p-calendar .p-inputtext {
    width: 100%;
    padding: 12px;
    border: 1px solid #DB0B00;
    border-radius: 4px;
  }
  .p-calendar .p-button {
    background-color: #DB0B00 !important; /* Rosso Corsa */
    border-color: #DB0B00 !important; /* Rosso Corsa */
  }
  .p-calendar .p-button-icon {
    color: white !important;
  }

.p-datepicker-prev{

}

  
  ::placeholder {
    color: #DB0B00 !important;
    opacity: 0.7;
  }
`;


  return (
    <div style={{ backgroundColor: '#FFD9F0', minHeight: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', padding: '20px' }}>
      <style>{customStyles}</style>
      <div style={{ maxWidth: '600px', width: '100%', padding: '30px', backgroundColor: '#FFB4E1', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' }}>
        <h1 style={{ color: '#DB0B00', textAlign: 'center', marginBottom: '20px', fontFamily: "'Bukhari Script', cursive", fontSize: '36px', fontWeight: 'normal' }}>Book Your Event</h1>
        <h2 style={{ fontFamily: "'Open Sans', sans-serif", fontWeight: 'bold', fontSize: '18px', color: '#DB0B00', marginBottom: '15px' }}>Fill out the form below to get started</h2>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="companyName" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Company Name</label>
          <InputText id="companyName" name="companyName" value={formData.companyName} onChange={handleChange} required placeholder="Enter company name" style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="contactName" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Contact Name</label>
          <InputText id="contactName" name="contactName" value={formData.contactName} onChange={handleChange} required placeholder="Enter contact name" style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="contactEmail" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Contact Email</label>
          <InputText id="contactEmail" name="contactEmail" value={formData.contactEmail} onChange={handleChange} required placeholder="Enter email address" style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="eventDates" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Event Date(s)</label>
          <Calendar
            id="eventDates"
            value={formData.eventDates}
            onChange={(e) => setFormData({ ...formData, eventDates: e.value })}
            selectionMode="multiple"
            minDate={minDate}
            maxDate={maxDate}
            showIcon
            readOnlyInput
            style={{ width: '100%' }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="eventTime" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Event Time</label>
          <Calendar id="eventTime" name="eventTime" value={formData.eventTime} onChange={(e) => setFormData({ ...formData, eventTime: e.value })} timeOnly showIcon hourFormat="24" readOnlyInput style={{ width: '100%' }} placeholder="Select event time" />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="eventType" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Event Type</label>
          <Dropdown id="eventType" name="eventType" value={formData.eventType} options={eventTypes} onChange={(e) => setFormData({ ...formData, eventType: e.value })} placeholder="Select Event Type" style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Will there be other food trucks or food offered at the event?</label>
          <div style={{ display: 'flex', gap: '10px' }}>
            <RadioButton inputId="otherFoodTrucksYes" name="otherFoodTrucks" value="yes" onChange={handleChange} checked={formData.otherFoodTrucks === 'yes'} />
            <label htmlFor="otherFoodTrucksYes" style={{ color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontSize: '16px', fontWeight: '700' }}>Yes</label>
            <RadioButton inputId="otherFoodTrucksNo" name="otherFoodTrucks" value="no" onChange={handleChange} checked={formData.otherFoodTrucks === 'no'} />
            <label htmlFor="otherFoodTrucksNo" style={{ color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontSize: '16px', fontWeight: '700' }}>No</label>
          </div>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="attendees" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Expected Number of Attendees</label>
          <InputNumber id="attendees" name="attendees" value={formData.attendees} onValueChange={(e) => setFormData({ ...formData, attendees: e.value })} style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} placeholder="Enter number of attendees" />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="additionalInfo" style={{ display: 'block', marginBottom: '8px', color: '#DB0B00', fontFamily: "'Open Sans', sans-serif", fontWeight: '600', fontSize: '16px' }}>Additional Information</label>
          <InputTextarea id="additionalInfo" name="additionalInfo" value={formData.additionalInfo} onChange={handleChange} rows={4} placeholder="Enter any additional information" style={{ width: '100%', padding: '12px', borderRadius: '4px', border: '1px solid #DB0B00' }} />
        </div>

        <Button label="Submit Booking Request" icon="pi pi-check" onClick={handleSubmit} style={{ backgroundColor: '#DB0B00', color: 'white', padding: '12px 24px', border: 'none', borderRadius: '4px', cursor: 'pointer', fontSize: '18px', fontWeight: 'bold' }} />

        <p style={{ fontFamily: "'Open Sans', sans-serif", fontStyle: 'italic', fontSize: '16px', color: '#DB0B00', marginTop: '20px', textAlign: 'center' }}>"Creating unforgettable events, one booking at a time."</p>
        <p style={{ fontFamily: "'Open Sans', sans-serif", fontWeight: '300', fontSize: '14px', color: '#DB0B00', marginTop: '10px', textAlign: 'center' }}>We look forward to making your event special!</p>
      </div>
    </div>
  );
};

export default BookingForm;
