import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Calendar } from 'primereact/calendar';
import { Dropdown } from 'primereact/dropdown';
import { InputNumber } from 'primereact/inputnumber';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';
import { InputTextarea } from 'primereact/inputtextarea';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    contactName: '',
    contactEmail: '',
    eventDates: null,
    eventTime: null,
    eventType: '',
    otherFoodTrucks: 'no',
    attendees: 0,
    additionalInfo: ''
  });

  const eventTypes = [
    { label: 'Corporate', value: 'corporate' },
    { label: 'Wedding', value: 'wedding' },
    { label: 'Birthday', value: 'birthday' },
    { label: 'Festival', value: 'festival' },
    { label: 'Other', value: 'other' }
  ];

  const today = new Date();
  const minDate = new Date(today.setDate(today.getDate() + 14)); // Two weeks from now
  const maxDate = new Date();
  maxDate.setFullYear(maxDate.getFullYear() + 2); // Two years from now

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Booking request submitted!');
  };

  const styles = {
    pageBackground: {
      backgroundColor: '#FFD9F0', // Pink Lace
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '20px',
      fontFamily: "'Open Sans', sans-serif",
    },
    container: {
      maxWidth: '600px',
      width: '100%',
      padding: '30px',
      backgroundColor: '#FFB4E1', // Light Hot Pink
      borderRadius: '8px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    header: {
      color: '#DB0B00', // Rosso Corsa
      textAlign: 'center',
      marginBottom: '20px',
      fontFamily: "'Bukhari Script', cursive",
      fontSize: '36px',
      fontWeight: 'normal',
    },
    subheading: {
      fontFamily: "'Open Sans', sans-serif",
      fontWeight: 'bold',
      fontSize: '18px',
      color: '#DB0B00',
      marginBottom: '15px',
    },
    field: {
      marginBottom: '20px',
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      color: '#DB0B00', // Rosso Corsa
      fontFamily: "'Open Sans', sans-serif",
      fontWeight: '600', // semibold
      fontSize: '16px',
    },
    input: {
      width: '100%',
      padding: '12px',
      borderRadius: '4px',
      border: '1px solid #DB0B00', // Rosso Corsa
      boxSizing: 'border-box',
      backgroundColor: 'white',
      fontFamily: "'Open Sans', sans-serif",
      fontStyle: 'italic',
      fontSize: '16px',
      fontWeight: '700',
      color: '#DB0B00', // Rosso Corsa
    },
    radioGroup: {
      display: 'flex',
      gap: '10px',
      fontFamily: "'Open Sans', sans-serif",
      fontSize: '16px',
    },
    radioLabel: {
      color: '#DB0B00', // Rosso Corsa
      fontFamily: "'Open Sans', sans-serif",
      fontSize: '16px',
      fontWeight: '700',
    },
    button: {
      backgroundColor: '#DB0B00', // Rosso Corsa
      color: 'white',
      padding: '12px 24px',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '18px',
      fontWeight: 'bold',
      fontFamily: "'Open Sans', sans-serif",
    },
    quote: {
      fontFamily: "'Open Sans', sans-serif",
      fontStyle: 'italic',
      fontSize: '16px',
      color: '#DB0B00',
      marginTop: '20px',
      textAlign: 'center',
    },
    caption: {
      fontFamily: "'Open Sans', sans-serif",
      fontWeight: '300', // light
      fontSize: '14px',
      color: '#DB0B00',
      marginTop: '10px',
      textAlign: 'center',
    },
    calendar: {
      width: '100%',
    }
  };

  const customStyles = `
    .p-calendar .p-datepicker-header {
      background-color: #DB0B00 !important; /* Rosso Corsa */
      color: white !important;
    }
    .p-calendar .p-datepicker-header .p-datepicker-title,
    .p-calendar .p-datepicker-header .p-datepicker-title .p-datepicker-month,
    .p-calendar .p-datepicker-header .p-datepicker-title .p-datepicker-year,
    .p-calendar table thead th {
      color: white !important;
    }
    .p-calendar .p-datepicker-header .p-datepicker-prev, 
    .p-calendar .p-datepicker-header .p-datepicker-next {
      color: #DB0B00 !important;
      background-color: white !important;
      border: 1px solid #DB0B00 !important;
      border-radius: 50%;
      width: 2rem;
      height: 2rem;
    }
    .p-calendar .p-datepicker-header .p-datepicker-prev:hover,
    .p-calendar .p-datepicker-header .p-datepicker-next:hover {
      background-color: #FFD9F0 !important; /* Lighter hover effect */
    }
    .p-datepicker table td > span {
      color: #DB0B00 !important;
    }
    .p-datepicker table td > span.p-highlight {
      background-color: #DB0B00 !important;
      color: white !important;
    }
    .p-datepicker table td.p-datepicker-today > span {
      background-color: #FFB4E1 !important;
      color: #DB0B00 !important;
    }
    .p-calendar .p-inputtext {
      border: 1px solid #DB0B00;
      border-radius: 4px;
    }
    .p-calendar .p-dropdown {
      background-color: white !important;
      color: #DB0B00 !important;
      border: 1px solid #DB0B00 !important;
      font-weight: 700 !important;
      font-family: 'Open Sans', sans-serif !important;
    }
    ::placeholder {
      color: #DB0B00 !important;
      opacity: 0.7;
    }
    input:-webkit-autofill {
      -webkit-text-fill-color: #DB0B00 !important;
      transition: background-color 5000s ease-in-out 0s;
    }
  `;

  return (
    <div style={styles.pageBackground}>
      <style>{customStyles}</style>
      <div style={styles.container}>
        <h1 style={styles.header}>Book Your Event</h1>
        <h2 style={styles.subheading}>Fill out the form below to get started</h2>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="companyName">Company Name</label>
          <InputText id="companyName" name="companyName" value={formData.companyName} onChange={handleChange} required style={styles.input} placeholder="Enter company name" />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="contactName">Contact Name</label>
          <InputText id="contactName" name="contactName" value={formData.contactName} onChange={handleChange} required style={styles.input} placeholder="Enter contact name" />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="contactEmail">Contact Email</label>
          <InputText id="contactEmail" name="contactEmail" value={formData.contactEmail} onChange={handleChange} required style={styles.input} placeholder="Enter email address" />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="eventDates">Event Date(s)</label>
          <Calendar
            id="eventDates"
            value={formData.eventDates}
            onChange={(e) => setFormData({ ...formData, eventDates: e.value })}
            selectionMode="multiple"
            minDate={minDate}
            maxDate={maxDate}
            monthNavigator
            yearNavigator
            yearRange={`${new Date().getFullYear()}:${new Date().getFullYear() + 2}`}
            readOnlyInput
            style={styles.calendar}
          />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="eventTime">Event Time</label>
          <Calendar id="eventTime" name="eventTime" value={formData.eventTime} onChange={(e) => setFormData({ ...formData, eventTime: e.value })} timeOnly hourFormat="24" style={styles.input} placeholder="Select event time" />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="eventType">Event Type</label>
          <Dropdown id="eventType" name="eventType" value={formData.eventType} options={eventTypes} onChange={(e) => setFormData({ ...formData, eventType: e.value })} placeholder="Select Event Type" style={styles.input} />
        </div>

        <div style={styles.field}>
          <label style={styles.label}>Will there be other food trucks or food offered at the event?</label>
          <div style={styles.radioGroup}>
            <RadioButton inputId="otherFoodTrucksYes" name="otherFoodTrucks" value="yes" onChange={handleChange} checked={formData.otherFoodTrucks === 'yes'} />
            <label htmlFor="otherFoodTrucksYes" style={styles.radioLabel}>Yes</label>
            <RadioButton inputId="otherFoodTrucksNo" name="otherFoodTrucks" value="no" onChange={handleChange} checked={formData.otherFoodTrucks === 'no'} />
            <label htmlFor="otherFoodTrucksNo" style={styles.radioLabel}>No</label>
          </div>
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="attendees">Expected Number of Attendees</label>
          <InputNumber id="attendees" name="attendees" value={formData.attendees} onValueChange={(e) => setFormData({ ...formData, attendees: e.value })} style={styles.input} placeholder="Enter number of attendees" />
        </div>

        <div style={styles.field}>
          <label style={styles.label} htmlFor="additionalInfo">Additional Information</label>
          <InputTextarea id="additionalInfo" name="additionalInfo" value={formData.additionalInfo} onChange={handleChange} style={styles.input} rows={4} placeholder="Enter any additional information" />
        </div>

        <Button label="Submit Booking Request" icon="pi pi-check" onClick={handleSubmit} style={styles.button} />

        <p style={styles.quote}>"Creating unforgettable events, one booking at a time."</p>
        <p style={styles.caption}>We look forward to making your event special!</p>
      </div>
    </div>
  );
};

export default BookingForm;
