import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Calendar } from 'primereact/calendar';
import { Dropdown } from 'primereact/dropdown';
import { InputNumber } from 'primereact/inputnumber';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';
import { InputTextarea } from 'primereact/inputtextarea';

const styles = {
  container: {
    backgroundColor: '#FFFFFF',
    margin: '20px auto',
    width: '90%',
    maxWidth: '600px',
    padding: '2rem',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    fontFamily: "'Open Sans', sans-serif"
  },
  title: {
    color: '#DB0B00',
    fontFamily: "'Open Sans Bold', sans-serif",
    marginBottom: '1.5rem'
  },
  label: {
    fontWeight: 'bold',
    marginBottom: '0.5rem',
    display: 'block',
    color: '#333333'
  },
  button: {
    backgroundColor: '#DB0B00',
    color: '#FFFFFF',
    border: 'none',
    padding: '12px 20px',
    borderRadius: '4px',
    fontSize: '1.1rem',
    fontFamily: "'Open Sans Semibold', sans-serif",
    cursor: 'pointer',
    width: '100%',
    textAlign: 'center',
    transition: 'background-color 0.3s ease'
  },
  buttonHover: {
    backgroundColor: '#FF1A1A',
  },
  formField: {
    marginBottom: '1.5rem'
  },
  calendar: {
    width: '100%',
    fontFamily: "'Open Sans', sans-serif"
  },
  dropdown: {
    width: '100%'
  },
  radioGroup: {
    display: 'flex',
    alignItems: 'center',
    marginTop: '10px',
  },
  radioLabel: {
    marginLeft: '5px',
    fontFamily: "'Open Sans', sans-serif"
  },
  radioButton: {
    marginRight: '20px'
  },
  input: {
    width: '100%',
    padding: '0.5rem',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontFamily: "'Open Sans', sans-serif"
  }
};

const BookingForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    contactName: '',
    contactEmail: '',
    eventDate: null,
    eventTime: null,
    eventType: '',
    otherFoodTrucks: 'no',
    attendees: 0,
    additionalInfo: ''
  });

  const eventTypes = [
    { label: 'Corporate', value: 'corporate' },
    { label: 'Wedding', value: 'wedding' },
    { label: 'Birthday', value: 'birthday' },
    { label: 'Festival', value: 'festival' },
    { label: 'Other', value: 'other' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Booking request submitted!');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Book Your Event</h2>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="companyName">Company Name</label>
        <InputText id="companyName" name="companyName" value={formData.companyName} onChange={handleChange} required style={styles.input} />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="contactName">Contact Name</label>
        <InputText id="contactName" name="contactName" value={formData.contactName} onChange={handleChange} required style={styles.input} />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="contactEmail">Contact Email</label>
        <InputText id="contactEmail" name="contactEmail" value={formData.contactEmail} onChange={handleChange} required style={styles.input} />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventDate">Event Date</label>
        <Calendar style={styles.calendar} id="eventDate" name="eventDate" value={formData.eventDate} onChange={(e) => setFormData({ ...formData, eventDate: e.value })} showIcon />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventTime">Event Time (Range)</label>
        <Calendar style={styles.calendar} id="eventTime" name="eventTime" value={formData.eventTime} onChange={(e) => setFormData({ ...formData, eventTime: e.value })} timeOnly hourFormat="24" showTime />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="eventType">Event Type</label>
        <Dropdown style={styles.dropdown} id="eventType" name="eventType" value={formData.eventType} options={eventTypes} onChange={(e) => setFormData({ ...formData, eventType: e.value })} placeholder="Select Event Type" />
      </div>

      <div style={styles.formField}>
        <label style={styles.label}>Will there be other food trucks or food offered at the event?</label>
        <div style={styles.radioGroup}>
          <div style={styles.radioButton}>
            <RadioButton inputId="otherFoodTrucksYes" name="otherFoodTrucks" value="yes" onChange={handleChange} checked={formData.otherFoodTrucks === 'yes'} />
            <label style={styles.radioLabel} htmlFor="otherFoodTrucksYes">Yes</label>
          </div>
          <div style={styles.radioButton}>
            <RadioButton inputId="otherFoodTrucksNo" name="otherFoodTrucks" value="no" onChange={handleChange} checked={formData.otherFoodTrucks === 'no'} />
            <label style={styles.radioLabel} htmlFor="otherFoodTrucksNo">No</label>
          </div>
        </div>
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="attendees">Expected Number of Attendees</label>
        <InputNumber id="attendees" name="attendees" value={formData.attendees} onChange={(e) => setFormData({ ...formData, attendees: e.value })} required style={styles.input} />
      </div>

      <div style={styles.formField}>
        <label style={styles.label} htmlFor="additionalInfo">Additional Information</label>
        <InputTextarea id="additionalInfo" name="additionalInfo" value={formData.additionalInfo} onChange={handleChange} style={styles.input} rows={4} />
      </div>

      <Button label="Submit Booking Request" icon="pi pi-check" onClick={handleSubmit} style={styles.button} />
    </div>
  );
};

export default BookingForm;
